1.2 local introshow         -- (TOP) External code will render here        -- Layer1 positioning        Layer1 = display.newImageRect(imgDir .. "p1_layer1.png", 768, 1024 );        Layer1.x = 384; Layer1.y = 512; Layer1.alpha = 1; Layer1.oldAlpha = 1        Layer1.oriX = Layer1.x; Layer1.oriY = Layer1.y        Layer1.name = "Layer1"        menuGroup:insert(1,Layer1); menuGroup.Layer1 = Layer1        -- pagen positioning        pagen = display.newImageRect(imgDir .. "p1_pagen.png", 71, 80 );        pagen.x = 732; pagen.y = 984; pagen.alpha = 1; pagen.oldAlpha = 1        pagen.oriX = pagen.x; pagen.oriY = pagen.y        pagen.name = "pagen"        menuGroup:insert(pagen); menuGroup.pagen = pagen        -- kwkBforward positioning        kwkBforward = display.newImageRect(imgDir .. "kwkbforward.png", 111, 87 );        kwkBforward.x = 475; kwkBforward.y = 971; kwkBforward.alpha = 1; kwkBforward.oldAlpha = 1        kwkBforward.oriX = kwkBforward.x; kwkBforward.oriY = kwkBforward.y        kwkBforward.name = "kwkBforward"        menuGroup:insert(kwkBforward); menuGroup.kwkBforward = kwkBforward        --V1.2 -- NOTE: The following code showed the outdated "intro show" dialog box. Removed in V1.2.       --V1.2 introshow positioning        --V1.2 introshow = display.newImageRect(imgDir .. "p1_introshow.png", 498, 510 );        --V1.2 introshow.x = 309; introshow.y = 654; introshow.alpha = 1; introshow.oldAlpha = 1        --V1.2 introshow.oriX = introshow.x; introshow.oriY = introshow.y        --V1.2 introshow.name = "introshow"        --V1.2 menuGroup:insert(introshow); menuGroup.introshow = introshow         -- Group(s) creation        -- (MIDDLE) External code will render here        -- Button event listeners        local function onpagenEvent(event)           pagen(pagen)           return true        end        pagen:addEventListener("tap", onpagenEvent )        local function onkwkBforwardEvent(event)           Cright(kwkBforward)           return true        end        kwkBforward:addEventListener("tap", onkwkBforwardEvent )        local function onintroshowEvent(event)           but_introshow_998(introshow)           return true        end        --V1.2 introshow:addEventListener("tap", onintroshowEvent )        -- Button functions        function pagen(self)             local myClosure_switch = function()                 dispose()                _G.kwk_currentPage = _G.kwk_menuPage                print("pagen: Go to page " .. _G.kwk_currentPage)                director:changeScene( "page_" .. _G.kwk_currentPage, "fade" )             end             timerStash.newTimer_976 = timer.performWithDelay(0, myClosure_switch, 1)        end        function Cright(self)             local myClosure_switch = function()                 dispose()                   _G.kwk_currentPage = _G.kwk_currentPage + 1                   print("Cright: Go forward to page " .. _G.kwk_currentPage)                   director:changeScene( "page_" .. _G.kwk_currentPage, "moveFromRight" )             end             timerStash.newTimer_976 = timer.performWithDelay(0, myClosure_switch, 1)        end         --V1.2 function but_introshow_998(self)         --V1.2   transitionStash.newTransition_977 = transition.to( introshow, {alpha=0, time=1000, delay=0})         --V1.2 end        -- swipe this page with spacer of 120 in normal direction        Gesture.activate( Layer1, {swipeLength=120} )        local function pageSwap(event )          print("PageSwap: current page is " .. _G.kwk_currentPage)         if event.phase == "ended" and event.direction ~= nil then              local wPage = _G.kwk_currentPage              local direction              if event.direction == "left" and kBidi == false then                 print("F. Event direction says: go left.")               wPage = _G.kwk_currentPage + 1                 if wPage > _G.kwk_lastPage then                print("Page number too high: " .. wPage)                print("Redirecting to last page.")                wPage = _G.kwk_lastPage               end                 direction = "moveFromRight"              elseif event.direction == "left" and kBidi == true then                 print("T. Event direction says: go left.")               wPage = _G.kwk_currentPage - 1                 if wPage < 1 then                print("Page number too low: " .. wPage)                print("Redirecting to menu page.")                wPage = _G.kwk_menuPage               end                 direction = "moveFromLeft"              elseif event.direction == "right" and kBidi == true then                 print("T. Event direction says: go right.")               wPage = _G.kwk_currentPage + 1                 if wPage > _G.kwk_lastPage then                print("Page number too high: " .. wPage)                print("Redirecting to last page.")                wPage = _G.kwk_lastPage               end                 direction = "moveFromRight"              elseif event.direction == "right" and kBidi == false then                 print("F. Event direction says: go right.")               _G.kwk_currentPage = _G.kwk_currentPage - 1                 if _G.kwk_currentPage < 1 then                print("Page number too low: " .. _G.kwk_currentPage)                wPage = _G.kwk_menuPage               end                 direction = "moveFromLeft"              end              print("From " .. _G.kwk_currentPage .. " to " .. wPage)            if wPage ~= _G.kwk_currentPage then dispose();                dispose()               print("Changing current page to " .. wPage .. ".")               _G.kwk_currentPage = wPage               director:changeScene("page_".._G.kwk_currentPage, direction)             end          end         end        Layer1:addEventListener( Gesture.SWIPE_EVENT, pageSwap )        dispose = function(event)           cancelAllTimers(); cancelAllTransitions()           audio.stop(32); audio.dispose(HeyDiddle64); HeyDiddle64 = nil           Layer1:removeEventListener( Gesture.SWIPE_EVENT, pageSwap ); Gesture.deactivate(Layer1)        end        -- (BOTTOM) External code will render here     end     drawScreen()     local myClosure_HeyDiddle64 = function()        audio.setVolume(0, { channel=32} );        audio.play(HeyDiddle64, {  channel=32, loops=0, fadein=500 } )     end     timerStash.newTimer_872 = timer.performWithDelay(0, myClosure_HeyDiddle64, 1)     return menuGroup end 