		navigation.DisplayDevelopmentVersion()
		menuGroup.navigator = navigation.InsertNavigation{}
       local tablesaw         local BG         BG = display.newImageRect(imageDir .. "p32_ch02pg8.png", 768, 1024 );        BG.x = 384; BG.y = 512; BG.alpha = 1; BG.oldAlpha = 1        menuGroup:insert(BG)        menuGroup.BG = BG        local ontablesawTouch = function(event)           if event.phase=="ended" then  --4             if gtStash.gt_tablesaw_563 then                gtStash.gt_tablesaw_563:toBeginning()             end             gtStash.gt_tablesaw_563= gtween.new( tablesaw, 3, { x = 384, y = 234, rotation = -360, xScale = 1, yScale = 1, alpha=1}, {ease = gtween.easing.linear, repeatCount = 1, reflect = false,  delay=0})              local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( tablesaw_audio, {channel=myChannel} )              end           end        end        tablesaw = ui.newButton{            defaultSrc= imageDir .."p32_tablesaw.png",            defaultX = 458,            defaultY = 461,            overSrc= imageDir .."p32_tablesaw.png",            overX = 458,            overY = 461,            onRelease=ontablesawTouch,            id="tablesawButton"        }        tablesaw.x = 384; tablesaw.y = 234; tablesaw.alpha = 1; tablesaw.oldAlpha = 1        menuGroup:insert(tablesaw)        menuGroup.tablesaw = tablesaw
	end
   drawScreen()    return menuGroup end 