      overSrc= imageDir .."p111_udder.png",            overX = 149,            overY = 124,            onRelease=onudderTouch,            id="udderButton"        }        udder.x = 126; udder.y = 842; udder.alpha = 1; udder.oldAlpha = 1        menuGroup:insert(udder)        menuGroup.udder = udder        local onudder2Touch = function(event)           if event.phase=="ended" then               local myChannel = 3              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( udder2_audio, {channel=myChannel} )              end           end        end        udder2 = ui.newButton{            defaultSrc= imageDir .."p111_udder2.png",            defaultX = 192,            defaultY = 152,            overSrc= imageDir .."p111_udder2.png",            overX = 192,            overY = 152,            onRelease=onudder2Touch,            id="udder2Button"        }        udder2.x = 160; udder2.y = 141; udder2.alpha = 1; udder2.oldAlpha = 1        menuGroup:insert(udder2)        menuGroup.udder2 = udder2        local onudder3Touch = function(event)           if event.phase=="ended" then               local myChannel = 4              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( udder3_audio, {channel=myChannel} )              end           end        end        udder3 = ui.newButton{            defaultSrc= imageDir .."p111_udder3.png",            defaultX = 139,            defaultY = 180,            overSrc= imageDir .."p111_udder3.png",            overX = 139,            overY = 180,            onRelease=onudder3Touch,            id="udder3Button"        }        udder3.x = 698; udder3.y = 658; udder3.alpha = 1; udder3.oldAlpha = 1        menuGroup:insert(udder3)        menuGroup.udder3 = udder3 --local PageZZCorner--local PageXXCorner--XpaperXcornerX              local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                    disposeTweens()                     director:changeScene( "page_" .. _G.kwk_currentPage-1 .. ".lua", "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                    disposeTweens()                     director:changeScene("page_" .. _G.kwk_currentPage+1 .. ".lua", "moveFromRight")                 end              end           end        end        CH7PG14:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(udder_audio); udder_audio = nil                           audio.stop(3); audio.dispose(udder2_audio); udder2_audio = nil                           audio.stop(4); audio.dispose(udder3_audio); udder3_audio = nil                            -- audio.stop(13); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 