		navigation.DisplayDevelopmentVersion()
		menuGroup.navigator = navigation.InsertNavigation{}
       local shot         local BG         BG = display.newImageRect(imageDir .. "p35_ch02pg11.png", 768, 1024 );        BG.x = 384; BG.y = 512; BG.alpha = 1; BG.oldAlpha = 1        menuGroup:insert(BG)        menuGroup.BG = BG        local onshotTouch = function(event)           if event.phase=="ended" then               local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( shot_audio, {channel=myChannel} )              end           end        end        shot = ui.newButton{            defaultSrc= imageDir .."p35_shot.png",            defaultX = 569,            defaultY = 299,            overSrc= imageDir .."p35_shot.png",            overX = 569,            overY = 299,            onRelease=onshotTouch,            id="shotButton"        }        shot.x = 394; shot.y = 758; shot.alpha = 1; shot.oldAlpha = 1        menuGroup:insert(shot)        menuGroup.shot = shot --local PageZZCorner
