		navigation.DisplayDevelopmentVersion()
		menuGroup.navigator = navigation.InsertNavigation{}       local thunder         local BG         BG = display.newImageRect(imageDir .. "p39_bg.png", 768, 1024 );        BG.x = 384; BG.y = 512; BG.alpha = 1; BG.oldAlpha = 1        menuGroup:insert(BG)        menuGroup.BG = BG        local onthunderTouch = function(event)           if event.phase=="ended" then               local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( thunder_audio, {channel=myChannel} )              end           end        end        thunder = ui.newButton{            defaultSrc= imageDir .."p39_thunder.png",            defaultX = 373,            defaultY = 393,            overSrc= imageDir .."p39_thunder.png",            overX = 373,            overY = 393,            onRelease=onthunderTouch,            id="thunderButton"        }        thunder.x = 249; thunder.y = 308; thunder.alpha = 1; thunder.oldAlpha = 1        menuGroup:insert(thunder)        menuGroup.thunder = thunder --local PageZZCorner	end   drawScreen()    return menuGroup end 