   id="chpt1mu2Button"        }        chpt1mu2.x = 374; chpt1mu2.y = 794; chpt1mu2.alpha = 1; chpt1mu2.oldAlpha = 1        menuGroup:insert(chpt1mu2)        menuGroup.chpt1mu2 = chpt1mu2               local onshowbutTouch = function(event)           if event.phase=="ended" then                  if showbox.alpha == 0 then                    transitionStash.newTransition_791 = transition.to( showbox, {alpha=showbox.oldAlpha, time=1000, delay=0})                 else                    transitionStash.newTransition_791 = transition.to( showbox, {alpha=0, time=1000, delay=0})                 end           end        end        showbut = ui.newButton{            defaultSrc= imageDir .."p19_showbut.png",            defaultX = 642,            defaultY = 345,            overSrc= imageDir .."p19_showbut.png",            overX = 642,            overY = 345,            onRelease=onshowbutTouch,            id="showbutButton"        }        showbut.x = 387; showbut.y = 172; showbut.alpha = 1; showbut.oldAlpha = 1        menuGroup:insert(showbut)        menuGroup.showbut = showbut        showbox = display.newImageRect(imageDir .. "p19_showbox.png", 491, 249 );        showbox.x = 372; showbox.y = 397; showbox.alpha = 1; showbox.oldAlpha = 1        menuGroup:insert(showbox)        menuGroup.showbox = showbox        staranima2 = display.newImageRect(imageDir .. "p19_staranima2.png", 16, 18 );        staranima2.x = -16; staranima2.y = 162; staranima2.alpha = 1; staranima2.oldAlpha = 1        menuGroup:insert(staranima2)        menuGroup.staranima2 = staranima2        showbox.alpha = 0        local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                    disposeTweens()                     director:changeScene( "page_" .. _G.kwk_currentPage-1 .. ".lua", "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                    disposeTweens()                     director:changeScene("page_" .. _G.kwk_currentPage+1 .. ".lua", "moveFromRight")                 end              end           end        end        BG:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(chpt1mu2_audio); chpt1mu2_audio = nil                            -- audio.stop(13); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 