up2Button"        }        chapt1mup2.x = 413; chapt1mup2.y = 539; chapt1mup2.alpha = 1; chapt1mup2.oldAlpha = 1        menuGroup:insert(chapt1mup2)        menuGroup.chapt1mup2 = chapt1mup2              local onshowbutTouch = function(event)           if event.phase=="ended" then                  if showbox.alpha == 0 then                    transitionStash.newTransition_698 = transition.to( showbox, {alpha=showbox.oldAlpha, time=1000, delay=0})                 else                    transitionStash.newTransition_698 = transition.to( showbox, {alpha=0, time=1000, delay=0})                 end           end        end        showbut = ui.newButton{            defaultSrc= imageDir .."p23_showbut.png",            defaultX = 425,            defaultY = 279,            overSrc= imageDir .."p23_showbut.png",            overX = 425,            overY = 279,            onRelease=onshowbutTouch,            id="showbutButton"        }        showbut.x = 381; showbut.y = 139; showbut.alpha = 1; showbut.oldAlpha = 1        menuGroup:insert(showbut)        menuGroup.showbut = showbut        showbox = display.newImageRect(imageDir .. "p23_showbox.png", 487, 391 );        showbox.x = 377; showbox.y = 405; showbox.alpha = 1; showbox.oldAlpha = 1        menuGroup:insert(showbox)        menuGroup.showbox = showbox        showbox.alpha = 0 
