utton = chButton(8, 390, 275, 290, 25)		chapter9button = chButton(9, 390, 305, 290, 25)		chapter10button = chButton(10, 390, 345, 290, 45)		chapter11button = chButton(11, 390, 385, 290, 25)		chapter12button = chButton(12, 390, 415, 290, 25)		chapter13button = chButton(13, 390, 445, 290, 25)		chapter14button = chButton(14, 390, 475, 290, 25)		creditsbutton = chButton(0, 420, 507, 110, 30)		staranima1 = display.newImageRect(imageDir .. "staranima1.png", 16, 16 ); 		staranima1.x = -12; staranima1.y = 101; staranima1.alpha = 1; staranima1.oldAlpha = 1 		menuGroup:insert(staranima1) 		menuGroup.staranima1 = staranima1 		gtStash.gt_staranima1_444= gtween.new( staranima1, 2.5, { x = 871, y = 618, rotation = 0, xScale = 1, yScale = 1, alpha=1}, {ease = gtween.easing.linear, repeatCount = 1, reflect = false,	delay=2}) 	end 	drawScreen() 	return menuGroup end 