    local file = io.open( path, "w+" ) 
    file:write( _G.kwk_currentPage ) 
    io.close( file ) 

    local drawScreen = function()		 navigation.DisplayDevelopmentVersion()		menuGroup.navigator = navigation.InsertNavigation{}        local showhide         local mint       local CH8PG1  --(2) regular layer        CH8PG1 = display.newImageRect( imageDir.. "p115_ch8pg1.png", 768, 1024 );        CH8PG1.x = 384; CH8PG1.y = 512; CH8PG1.alpha = 1; CH8PG1.oldAlpha = 1        menuGroup:insert(CH8PG1)        menuGroup.CH8PG1 = CH8PG1         local onmintTouch = function(event)           if event.phase=="ended" then                  if showhide.alpha == 0 then                    transitionStash.newTransition_496 = transition.to( showhide, {alpha=showhide.oldAlpha, time=1000, delay=0})                 else                    transitionStash.newTransition_496 = transition.to( showhide, {alpha=0, time=1000, delay=0})                 end              local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( mint_audio, {channel=myChannel} )              end           end        end --(10) regular layer        mint = ui.newButton{            defaultSrc=imageDir.."p115_mint.png",            defaultX = 181,            defaultY = 368,            overSrc=imageDir.."p115_mint.png",            overX = 181,            overY = 368,            onRelease=onmintTouch,            id="mintButton"        }        mint.x = 163; mint.y = 312; mint.alpha = 1; mint.oldAlpha = 1        menuGroup:insert(mint)        menuGroup.mint = mint --(2) regular layer        showhide = display.newImageRect( imageDir.. "p115_showhide.png", 412, 399 );        showhide.x = 445; showhide.y = 285; showhide.alpha = 1; showhide.oldAlpha = 1        menuGroup:insert(showhide)        menuGroup.showhide = showhide        showhide.alpha = 0          local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < numPages) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH8PG1:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(mint_audio); mint_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 