    local menuGroup = display.newGroup() 
    local disposeAudios     local disposeTweens     local spoony_audio = audio.loadSound(audioDir.."click.mp3" )     local path = system.pathForFile( "SpoonAndTheMoonPart2.txt", system.DocumentsDirectory ) 
    local file = io.open( path, "w+" ) 
    file:write( _G.kwk_currentPage ) 
    io.close( file ) 

    local drawScreen = function()

		 navigation.DisplayDevelopmentVersion()
		menuGroup.navigator = navigation.InsertNavigation{}


local showhide         local spoony         local CH10PG3  --(2) regular layer        CH10PG3 = display.newImageRect( imageDir.. "p147_ch10pg3.png", 768, 1024 );        CH10PG3.x = 384; CH10PG3.y = 512; CH10PG3.alpha = 1; CH10PG3.oldAlpha = 1        menuGroup:insert(CH10PG3)        menuGroup.CH10PG3 = CH10PG3        local onspoonyTouch = function(event)           if event.phase=="ended" then                  if showhide.alpha == 0 then                    transitionStash.newTransition_488 = transition.to( showhide, {alpha=showhide.oldAlpha, time=1000, delay=0})                 else                    transitionStash.newTransition_488 = transition.to( showhide, {alpha=0, time=1000, delay=0})                 end              local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( spoony_audio, {channel=myChannel} )              end           end        end --(10) regular layer        spoony = ui.newButton{            defaultSrc=imageDir.."p147_spoony.png",            defaultX = 129,            defaultY = 497,            overSrc=imageDir.."p147_spoony.png",            overX = 129,            overY = 497,            onRelease=onspoonyTouch,            id="spoonyButton"        }        spoony.x = 116; spoony.y = 741; spoony.alpha = 1; spoony.oldAlpha = 1        menuGroup:insert(spoony)        menuGroup.spoony = spoony --(2) regular layer        showhide = display.newImageRect( imageDir.. "p147_showhide.png", 412, 246 );        showhide.x = 334; showhide.y = 554; showhide.alpha = 1; showhide.oldAlpha = 1        menuGroup:insert(showhide)        menuGroup.showhide = showhide        showhide.alpha = 0          local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < numPages) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH10PG3:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(spoony_audio); spoony_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 