    local menuGroup = display.newGroup() 
    local disposeAudios     local disposeTweens     local saw_audio = audio.loadSound(audioDir.."chainsaw.mp3" )     local saw2_audio = audio.loadSound(audioDir.."machine.mp3" )     local saw3_audio = audio.loadSound(audioDir.."saw2.mp3" )     local path = system.pathForFile( "SpoonAndTheMoonPart2.txt", system.DocumentsDirectory ) 
