    local drawScreen = function()

		 navigation.DisplayDevelopmentVersion()
		menuGroup.navigator = navigation.InsertNavigation{}


local saw3         local saw2         local saw         local CH10PG7  --(2) regular layer        CH10PG7 = display.newImageRect( imageDir.. "p151_ch10pg7.png", 768, 1024 );        CH10PG7.x = 384; CH10PG7.y = 512; CH10PG7.alpha = 1; CH10PG7.oldAlpha = 1        menuGroup:insert(CH10PG7)        menuGroup.CH10PG7 = CH10PG7        local onsawTouch = function(event)           if event.phase=="ended" then               local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( saw_audio, {channel=myChannel} )              end           end        end --(10) regular layer        saw = ui.newButton{            defaultSrc=imageDir.."p151_saw.png",            defaultX = 145,            defaultY = 110,            overSrc=imageDir.."p151_saw.png",            overX = 145,            overY = 110,            onRelease=onsawTouch,            id="sawButton"        }        saw.x = 72; saw.y = 252; saw.alpha = 1; saw.oldAlpha = 1        menuGroup:insert(saw)        menuGroup.saw = saw        local onsaw2Touch = function(event)           if event.phase=="ended" then               local myChannel = 3              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( saw2_audio, {channel=myChannel} )              end           end        end --(10) regular layer        saw2 = ui.newButton{            defaultSrc=imageDir.."p151_saw2.png",            defaultX = 208,            defaultY = 128,            overSrc=imageDir.."p151_saw2.png",            overX = 208,            overY = 128,            onRelease=onsaw2Touch,            id="saw2Button"        }        saw2.x = 365; saw2.y = 180; saw2.alpha = 1; saw2.oldAlpha = 1        menuGroup:insert(saw2)        menuGroup.saw2 =