    local menuGroup = display.newGroup() 
    local disposeAudios     local disposeTweens     local shunday_audio = audio.loadSound(audioDir.."click.mp3" )     local path = system.pathForFile( "SpoonAndTheMoonPart2.txt", system.DocumentsDirectory ) 
    local file = io.open( path, "w+" ) 
    file:write( _G.kwk_currentPage ) 
    io.close( file ) 

    local drawScreen = function()

		 navigation.DisplayDevelopmentVersion()
		menuGroup.navigator = navigation.InsertNavigation{}


local showhide         local shunday         local CH13PG3  --(2) regular layer        CH13PG3 = display.newImageRect( imageDir.. "p194_ch13pg3.png", 768, 1024 );        CH13PG3.x = 384; CH13PG3.y = 512; CH13PG3.alpha = 1; CH13PG3.oldAlpha = 1        menuGroup:insert(CH13PG3)        menuGroup.CH13PG3 = CH13PG3        local onshundayTouch = function(event)           if event.phase=="ended" then                  if showhide.alpha == 0 then                    transitionStash.newTransition_586 = transition.to( showhide, {alpha=showhide.oldAlpha, time=1000, delay=0})                 else                    transitionStash.newTransition_586 = transition.to( showhide, {alpha=0, time=1000, delay=0})                 end              local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( shunday_audio, {channel=myChannel} )              end           end        end --(10) regular layer        shunday = ui.newButton{            defaultSrc=imageDir.."p194_shunday.png",            defaultX = 209,            defaultY = 266,            overSrc=imageDir.."p194_shunday.png",            overX = 209,            overY = 266,            onRelease=onshundayTouch,            id="shundayButton"        }        shunday.x = 626; shunday.y = 614; shunday.alpha = 1; shunday.oldAlpha = 1        menuGroup:insert(shunday)        menuGroup.shunday = shunday --(2) regular layer        showhide = display.newImageRect( imageDir.. "p194_showhide.png", 387, 225 );        showhide.x = 361; showhide.y = 542; showhide.alpha = 1; showhide.oldAlpha = 1        menuGroup:insert(showhide)        menuGroup.showhide = showhide        showhide.alpha = 0          local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < numPages) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH13PG3:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(shunday_audio); shunday_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 