= "NavigationButtonForward",		imageFile = imageDir .. "kwkBforward.png",		alpha = 1,		width = 111,		height = 88,		x = 475,		y = 975,		transitionEffect = "moveFromRight",		touchSound = "click.mp3",	},	defaultMenuButton = {		category = "navigation button",		name = "NavigationButtonMenu",		imageFile = imageDir .. "kwkBindex.png",		alpha = 1,		width = 66,		height = 69,		x = 383,		y = 969,		transitionEffect = "overFromTop",		imageFile = imageDir .. "kwkBindex.png",		touchSound = audioDir .. "indexflip.mp3",	},	defaultPageCornerButton = {		category = "navigation button",		name = "NavigationButtonCorner",		imageFile = imageDir .. "PageCornerGrey.png",		alpha = 1,		width = 72,		height = 80,		x = 732,		y = 984,		transitionEffect = "moveFromLeft",		touchSound = "click.mp3",	},	defaultPageNumberText = {		category = "text",		alpha = 1, -- doesn't get used		width = 72,		height = 80,		x1d = 744,		x2d = 737,		x3d = 730,		x = 752,		xOffset = 8,		y = 995,		fontname = "Arbutus Slab",		fontsize = 20,		name = "NavigationPageNumber",	},	navigationScheme = "Standard",}-- Lists lua files containing definitions we need.director = require("director") ui = require("ui")navigation = require("gt_navigation")soundeffects = require("gt_sound")add = require("gt_addtopage")local gtween = require("gtween") -- Sets whether they can see the clock etc.display.setStatusBar( display.HiddenStatusBar ) -- vs. display.setStatusBar( display.DefaultStatusBar ) _G.lang = "" kBidi = false kAutoPlay = 0local path = system.pathForFile( "SpoonAndTheMoonPart2.txt", system.DocumentsDirectory ) local file = io.open( path, "r" ) if file then 	local kBookmark = 0	bookmarkedPage = file:read("*number") 	if type(bookmarkedPage) == "number" and bookmarkedPage >= _G.kwk_firstPage and bookmarkedPage <= _G.kwk_lastPage then kBookmark = 1 end	if _G.kwk_ShowDebugOutput then		print("Bookmark file exists!")		print("Starting page: " .. bookmarkedPage)		print("Is it legit: " .. kBookmark)	end	if kBookmark > 0 then _G.kwk_currentPage = bookmarkedPage end 	io.close(file) else 	print("Bookmark file does not exist yet!")	local file = io.open( path, "w+b" ) 	file:write( "1" ) 	kBookmark = 1 	io.close(file) end --Create a main grouplocal mainGroup = display.newGroup()-- Main functionlocal function main()	-- Add the group from Director calls	-- local navigation_audio = audio.loadSound(audioDir .. "indexflip.mp3", system.ResourceDirectory)	mainGroup:insert(director.directorView)	 -- This is how Corona knows to begin with page_1.lua	if _G.kwk_ShowDebugOutput then		print("")		print("Opening first page from main().")		print("Current page would be: " .. _G.kwk_currentPage)		print("But we're debugging, so let's open the menu page instead.")		_G.kwk_currentPage = _G.kwk_menuPage		print(_G.kwk_menuPage)	end--if	director:changeScene("page_" .. _G.kwk_currentPage)	return trueend--Clear timers and transitionstimerStash = {}transitionStash = {}gtStash = {}function cancelAllSounds()	local i	for i = 1, 32, 1 do		isChannelPlaying = audio.isChannelPlaying(i) 		if isChannelPlaying then audio.stop(i) end		isChannelActive = audio.isChannelActive(i) 		if isChannelActive then audio.dispose(i) end	end--for-loopend--function end-all-soundsfunction cancelAllTimers()		local k, v		for k,v in pairs(timerStash) do				timer.cancel( v )				v = nil; k = nil		end		timerStash = nil		timerStash = {}endfunction cancelAllTransitions()		local k, v		for k,v in pairs(transitionStash) do				transition.cancel( v )				v = nil; k = nil		end		transitionStash = nil		transitionStash = {}end--cancel all gtweensfunction cancelAllTweens()		local k, v		for k,v in pairs(gtStash) do				v:pause();				v = nil; k = nil		end		gtStash = nil		gtStash = {}endmain()