       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
       local showhidhe         local tools          local CH15PG1  --(2) regular layer        CH15PG1 = display.newImageRect( imageDir.. "p209_ch15pg1.png", 768, 1024 );        CH15PG1.x = 384; CH15PG1.y = 512; CH15PG1.alpha = 1; CH15PG1.oldAlpha = 1        menuGroup:insert(CH15PG1)        menuGroup.CH15PG1 = CH15PG1 --pprcrnr       local ontoolsTouch = function(event)           if event.phase=="ended" then                  if showhidhe.alpha == 0 then                    transitionStash.newTransition_892 = transition.to( showhidhe, {alpha=showhidhe.oldAlpha, time=1000, delay=0})                 else                    transitionStash.newTransition_892 = transition.to( showhidhe, {alpha=0, time=1000, delay=0})                 end              local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( tools_audio, {channel=myChannel} )              end           end        end --(10) regular layer        tools = ui.newButton{            defaultSrc=imageDir.."p209_tools.png",            defaultX = 206,            defaultY = 363,            overSrc=imageDir.."p209_tools.png",            overX = 206,            overY = 363,            onRelease=ontoolsTouch,            id="toolsButton"        }        tools.x = 151; tools.y = 292; tools.alpha = 1; tools.oldAlpha = 1        menuGroup:insert(tools)        menuGroup.tools = tools --(2) regular layer        showhidhe = display.newImageRect( imageDir.. "p209_showhidhe.png", 412, 317 );        showhidhe.x = 414; showhidhe.y = 304; showhidhe.alpha = 1; showhidhe.oldAlpha = 1        menuGroup:insert(showhidhe)        menuGroup.showhidhe = showhidhe --nav--navIn--nav       showhidhe.alpha = 0          local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH15PG1:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(tools_audio); tools_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 