       local spoons          local CH15PG3  --(2) regular layer        CH15PG3 = display.newImageRect( imageDir.. "p211_ch15pg3.png", 768, 1024 );        CH15PG3.x = 384; CH15PG3.y = 512; CH15PG3.alpha = 1; CH15PG3.oldAlpha = 1        menuGroup:insert(CH15PG3)        menuGroup.CH15PG3 = CH15PG3 --pprcrnr       local onspoonsTouch = function(event)           if event.phase=="ended" then               local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( spoons_audio, {channel=myChannel} )              end           end        end --(10) regular layer        spoons = ui.newButton{            defaultSrc=imageDir.."p211_spoons.png",            defaultX = 191,            defaultY = 322,            overSrc=imageDir.."p211_spoons.png",            overX = 191,            overY = 322,            onRelease=onspoonsTouch,            id="spoonsButton"        }        spoons.x = 161; spoons.y = 715; spoons.alpha = 1; spoons.oldAlpha = 1        menuGroup:insert(spoons)        menuGroup.spoons = spoons --nav--navIn--nav         local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH15PG3:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(spoons_audio); spoons_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 