       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
       local shohwhide         local cigar          local CH15PG4  --(2) regular layer        CH15PG4 = display.newImageRect( imageDir.. "p212_ch15pg4.png", 768, 1024 );        CH15PG4.x = 384; CH15PG4.y = 512; CH15PG4.alpha = 1; CH15PG4.oldAlpha = 1        menuGroup:insert(CH15PG4)        menuGroup.CH15PG4 = CH15PG4 --pprcrnr       local oncigarTouch = function(event)           if event.phase=="ended" then                  if shohwhide.alpha == 0 then                    transitionStash.newTransition_344 = transition.to( shohwhide, {alpha=shohwhide.oldAlpha, time=1000, delay=0})                 else                    transitionStash.newTransition_344 = transition.to( shohwhide, {alpha=0, time=1000, delay=0})                 end              local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( cigar_audio, {channel=myChannel} )              end           end        end --(10) regular layer        cigar = ui.newButton{            defaultSrc=imageDir.."p212_cigar.png",            defaultX = 109,            defaultY = 121,            overSrc=imageDir.."p212_cigar.png",            overX = 109,            overY = 121,            onRelease=oncigarTouch,            id="cigarButton"        }        cigar.x = 73; cigar.y = 797; cigar.alpha = 1; cigar.oldAlpha = 1        menuGroup:insert(cigar)        menuGroup.cigar = cigar --(2) regular layer        shohwhide = display.newImageRect( imageDir.. "p212_shohwhide.png", 386, 159 );        shohwhide.x = 268; shohwhide.y = 787; shohwhide.alpha = 1; shohwhide.oldAlpha = 1        menuGroup:insert(shohwhide)        menuGroup.shohwhide = shohwhide --nav--navIn--nav       shohwhide.alpha = 0          local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart