       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
        local cat         local CH16PG1  --(2) regular layer        CH16PG1 = display.newImageRect( imageDir.. "p228_ch16pg1.png", 768, 1024 );        CH16PG1.x = 384; CH16PG1.y = 512; CH16PG1.alpha = 1; CH16PG1.oldAlpha = 1        menuGroup:insert(CH16PG1)        menuGroup.CH16PG1 = CH16PG1        local oncatTouch = function(event)           if event.phase=="ended" then               local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( cat_audio, {channel=myChannel} )              end           end        end --(10) regular layer        cat = ui.newButton{            defaultSrc=imageDir.."p228_cat.png",            defaultX = 525,            defaultY = 55,            overSrc=imageDir.."p228_cat.png",            overX = 525,            overY = 55,            onRelease=oncatTouch,            id="catButton"        }        cat.x = 364; cat.y = 261; cat.alpha = 1; cat.oldAlpha = 1        menuGroup:insert(cat)        menuGroup.cat = cat --pprcrnr--nav--navIn--nav         local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH16PG1:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(cat_audio); cat_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 