    local drawScreen = function() 

       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
        local showhide         local moth         local sob2         local CH17PG2  --(2) regular layer        CH17PG2 = display.newImageRect( imageDir.. "p233_ch17pg2.png", 768, 1024 );        CH17PG2.x = 384; CH17PG2.y = 512; CH17PG2.alpha = 1; CH17PG2.oldAlpha = 1        menuGroup:insert(CH17PG2)        menuGroup.CH17PG2 = CH17PG2        local onsob2Touch = function(event)           if event.phase=="ended" then               local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( sob2_audio, {channel=myChannel} )              end           end        end --(10) regular layer        sob2 = ui.newButton{            defaultSrc=imageDir.."p233_sob2.png",            defaultX = 432,            defaultY = 44,            overSrc=imageDir.."p233_sob2.png",            overX = 432,            overY = 44,            onRelease=onsob2Touch,            id="sob2Button"        }        sob2.x = 387; sob2.y = 882; sob2.alpha = 1; sob2.oldAlpha = 1        menuGroup:insert(sob2)        menuGroup.sob2 = sob2        local onmothTouch = function(event)           if event.phase=="ended" then                  if showhide.alpha == 0 then                    transitionStash.newTransition_588 = transition.to( showhide, {alpha=showhide.oldAlpha, time=1000, delay=0})                 else                    transitionStash.newTransition_588 = transition.to( showhide, {alpha=0, time=1000, delay=0})                 end              local myChannel = 3              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( moth_audio, {channel=myChannel} )              end           end        end --(10) regular layer        moth = ui.newButton{            defaultSrc=imageDir.."p233_moth.png",            defaultX = 223,            defaultY = 71,            overSrc=imageDir.."p233_moth.png",            overX = 223,            overY = 71,            onRelease=onmothTouch,            id="mothButton"        }        moth.x = 221; moth.y = 47; moth.alpha = 1; moth.oldAlpha = 1        menuGroup:insert(moth)        menuGroup.moth = moth --(2) regular layer        showhide = display.newImageRect( imageDir.. "p233_showhide.png", 592, 197 );        showhide.x = 361; showhide.y = 143; showhide.alpha = 1; showhide.oldAlpha = 1        menuGroup:insert(showhide)        menuGroup.showhide = showhide --pprcrnr--nav--navIn--nav       showhide.alpha = 0          local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH17PG2:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(sob2_audio); sob2_audio = nil                           audio.stop(3); audio.dispose(moth_audio); moth_audio = nil                           audio.stop(4); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 