       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
        local starzap         local CH17PG10  --(2) regular layer        CH17PG10 = display.newImageRect( imageDir.. "p241_ch17pg10.png", 768, 1024 );        CH17PG10.x = 384; CH17PG10.y = 512; CH17PG10.alpha = 1; CH17PG10.oldAlpha = 1        menuGroup:insert(CH17PG10)        menuGroup.CH17PG10 = CH17PG10        local onstarzapTouch = function(event)           if event.phase=="ended" then               local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( starzap_audio, {channel=myChannel} )              end           end        end --(10) regular layer        starzap = ui.newButton{            defaultSrc=imageDir.."p241_starzap.png",            defaultX = 286,            defaultY = 281,            overSrc=imageDir.."p241_starzap.png",            overX = 286,            overY = 281,            onRelease=onstarzapTouch,            id="starzapButton"        }        starzap.x = 580; starzap.y = 262; starzap.alpha = 1; starzap.oldAlpha = 1        menuGroup:insert(starzap)        menuGroup.starzap = starzap --pprcrnr--nav--navIn--nav         local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH17PG10:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(starzap_audio); starzap_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 