    local drawScreen = function() 

       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
       local showhide         local les          local CH18PG1  --(2) regular layer        CH18PG1 = display.newImageRect( imageDir.. "p245_ch18pg1.png", 768, 1024 );        CH18PG1.x = 384; CH18PG1.y = 512; CH18PG1.alpha = 1; CH18PG1.oldAlpha = 1        menuGroup:insert(CH18PG1)        menuGroup.CH18PG1 = CH18PG1 --pprcrnr       local onlesTouch = function(event)           if event.phase=="ended" then                  if showhide.alpha == 0 then                    transitionStash.newTransition_161 = transition.to( showhide, {alpha=showhide.oldAlpha, time=1000, delay=0})                 else                    transitionStash.newTransition_161 = transition.to( showhide, {alpha=0, time=1000, delay=0})                 end              local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( les_audio, {channel=myChannel} )              end           end        end --(10) regular layer        les = ui.newButton{            defaultSrc=imageDir.."p245_les.png",            defaultX = 475,            defaultY = 257,            overSrc=imageDir.."p245_les.png",            overX = 475,            overY = 257,            onRelease=onlesTouch,            id="lesButton"        }        les.x = 328; les.y = 787; les.alpha = 1; les.oldAlpha = 1        menuGroup:insert(les)        menuGroup.les = les --(2) regular layer        showhide = display.newImageRect( imageDir.. "p245_showhide.png", 344, 224 );        showhide.x = 196; showhide.y = 678; showhide.alpha = 1; showhide.oldAlpha = 1        menuGroup:insert(showhide)        menuGroup.showhide = showhide --nav--navIn--nav       showhide.alpha = 0          local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH18PG1:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(les_audio); les_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 