       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
        local skip         local CH18PG14  --(2) regular layer        CH18PG14 = display.newImageRect( imageDir.. "p258_ch18pg14.png", 768, 1024 );        CH18PG14.x = 384; CH18PG14.y = 512; CH18PG14.alpha = 1; CH18PG14.oldAlpha = 1        menuGroup:insert(CH18PG14)        menuGroup.CH18PG14 = CH18PG14        local onskipTouch = function(event)           if event.phase=="ended" then               local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( skip_audio, {channel=myChannel} )              end           end        end --(10) regular layer        skip = ui.newButton{            defaultSrc=imageDir.."p258_skip.png",            defaultX = 312,            defaultY = 27,            overSrc=imageDir.."p258_skip.png",            overX = 312,            overY = 27,            onRelease=onskipTouch,            id="skipButton"        }        skip.x = 453; skip.y = 233; skip.alpha = 1; skip.oldAlpha = 1        menuGroup:insert(skip)        menuGroup.skip = skip --pprcrnr--nav--navIn--nav         local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH18PG14:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(skip_audio); skip_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 