       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
       local showhid         local popsicle          local CH18PG15  --(2) regular layer        CH18PG15 = display.newImageRect( imageDir.. "p259_ch18pg15.png", 768, 1024 );        CH18PG15.x = 384; CH18PG15.y = 512; CH18PG15.alpha = 1; CH18PG15.oldAlpha = 1        menuGroup:insert(CH18PG15)        menuGroup.CH18PG15 = CH18PG15 --pprcrnr       local onpopsicleTouch = function(event)           if event.phase=="ended" then                  if showhid.alpha == 0 then                    transitionStash.newTransition_764 = transition.to( showhid, {alpha=showhid.oldAlpha, time=1000, delay=0})                 else                    transitionStash.newTransition_764 = transition.to( showhid, {alpha=0, time=1000, delay=0})                 end              local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( popsicle_audio, {channel=myChannel} )              end           end        end --(10) regular layer        popsicle = ui.newButton{            defaultSrc=imageDir.."p259_popsicle.png",            defaultX = 95,            defaultY = 393,            overSrc=imageDir.."p259_popsicle.png",            overX = 95,            overY = 393,            onRelease=onpopsicleTouch,            id="popsicleButton"        }        popsicle.x = 47; popsicle.y = 626; popsicle.alpha = 1; popsicle.oldAlpha = 1        menuGroup:insert(popsicle)        menuGroup.popsicle = popsicle --(2) regular layer        showhid = display.newImageRect( imageDir.. "p259_showhid.png", 412, 275 );        showhid.x = 254; showhid.y = 548; showhid.alpha = 1; showhid.oldAlpha = 1        menuGroup:insert(showhid)        menuGroup.showhid = showhid --nav--navIn--nav       showhid.alpha = 0          local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH18PG15:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(popsicle_audio); popsicle_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 