       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
        local showhide         local undies         local CH19PG1  --(2) regular layer        CH19PG1 = display.newImageRect( imageDir.. "p262_ch19pg1.png", 768, 1024 );        CH19PG1.x = 384; CH19PG1.y = 512; CH19PG1.alpha = 1; CH19PG1.oldAlpha = 1        menuGroup:insert(CH19PG1)        menuGroup.CH19PG1 = CH19PG1        local onundiesTouch = function(event)           if event.phase=="ended" then                  if showhide.alpha == 0 then                    transitionStash.newTransition_814 = transition.to( showhide, {alpha=showhide.oldAlpha, time=1000, delay=0})                 else                    transitionStash.newTransition_814 = transition.to( showhide, {alpha=0, time=1000, delay=0})                 end              local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( undies_audio, {channel=myChannel} )              end           end        end --(10) regular layer        undies = ui.newButton{            defaultSrc=imageDir.."p262_undies.png",            defaultX = 662,            defaultY = 88,            overSrc=imageDir.."p262_undies.png",            overX = 662,            overY = 88,            onRelease=onundiesTouch,            id="undiesButton"        }        undies.x = 395; undies.y = 77; undies.alpha = 1; undies.oldAlpha = 1        menuGroup:insert(undies)        menuGroup.undies = undies --(2) regular layer        showhide = display.newImageRect( imageDir.. "p262_showhide.png", 512, 173 );        showhide.x = 355; showhide.y = 182; showhide.alpha = 1; showhide.oldAlpha = 1        menuGroup:insert(showhide)        menuGroup.showhide = showhide --pprcrnr--nav--navIn--nav       showhide.alpha = 0          local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH19PG1:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(undies_audio); undies_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 