       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
        local shatter         local CH19PG7  --(2) regular layer        CH19PG7 = display.newImageRect( imageDir.. "p268_ch19pg7.png", 768, 1024 );        CH19PG7.x = 384; CH19PG7.y = 512; CH19PG7.alpha = 1; CH19PG7.oldAlpha = 1        menuGroup:insert(CH19PG7)        menuGroup.CH19PG7 = CH19PG7        local onshatterTouch = function(event)           if event.phase=="ended" then               local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( shatter_audio, {channel=myChannel} )              end           end        end --(10) regular layer        shatter = ui.newButton{            defaultSrc=imageDir.."p268_shatter.png",            defaultX = 255,            defaultY = 265,            overSrc=imageDir.."p268_shatter.png",            overX = 255,            overY = 265,            onRelease=onshatterTouch,            id="shatterButton"        }        shatter.x = 188; shatter.y = 733; shatter.alpha = 1; shatter.oldAlpha = 1        menuGroup:insert(shatter)        menuGroup.shatter = shatter --pprcrnr--nav--navIn--nav         local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH19PG7:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(shatter_audio); shatter_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 