    local drawScreen = function() 

       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
       local showhide         local moon          local CH19PG8  --(2) regular layer        CH19PG8 = display.newImageRect( imageDir.. "p269_ch19pg8.png", 768, 1024 );        CH19PG8.x = 384; CH19PG8.y = 512; CH19PG8.alpha = 1; CH19PG8.oldAlpha = 1        menuGroup:insert(CH19PG8)        menuGroup.CH19PG8 = CH19PG8 --pprcrnr       local onmoonTouch = function(event)           if event.phase=="ended" then                  if showhide.alpha == 0 then                    transitionStash.newTransition_700 = transition.to( showhide, {alpha=showhide.oldAlpha, time=1000, delay=0})                 else                    transitionStash.newTransition_700 = transition.to( showhide, {alpha=0, time=1000, delay=0})                 end              local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( moon_audio, {channel=myChannel} )              end           end        end --(10) regular layer        moon = ui.newButton{            defaultSrc=imageDir.."p269_moon.png",            defaultX = 280,            defaultY = 274,            overSrc=imageDir.."p269_moon.png",            overX = 280,            overY = 274,            onRelease=onmoonTouch,            id="moonButton"        }        moon.x = 595; moon.y = 209; moon.alpha = 1; moon.oldAlpha = 1        menuGroup:insert(moon)        menuGroup.moon = moon --(2) regular layer        showhide = display.newImageRect( imageDir.. "p269_showhide.png", 393, 203 );        showhide.x = 303; showhide.y = 152; showhide.alpha = 1; showhide.oldAlpha = 1        menuGroup:insert(showhide)        menuGroup.showhide = showhide --nav--navIn--nav       showhide.alpha = 0          local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH19PG8:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(moon_audio); moon_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 