    local drawScreen = function()        navigation.DisplayDevelopmentVersion()       menuGroup.navigator = navigation.InsertNavigation{}       local chpt1pv         local chpt1pvover          local CH19PG13  --(2) regular layer        CH19PG13 = display.newImageRect( imageDir.. "p274_ch19pg13.png", 768, 1024 );        CH19PG13.x = 384; CH19PG13.y = 512; CH19PG13.alpha = 1; CH19PG13.oldAlpha = 1        menuGroup:insert(CH19PG13)        menuGroup.CH19PG13 = CH19PG13 --pprcrnr       local onchpt1pvTouch = function(event)           if event.phase=="ended" then           media.playVideo( "movie_toast.mp4", system.ResourceDirectory, false )           end        end --(10) regular layer        chpt1pv = ui.newButton{            defaultSrc=imageDir.."p274_chpt1pv.png",            defaultX = 102,            defaultY = 102,            overSrc=imageDir.."p274_chpt1pvover.png",            overX = 102,            overY = 102,            onRelease=onchpt1pvTouch,            id="chpt1pvButton"        }        chpt1pv.x = 380; chpt1pv.y = 802; chpt1pv.alpha = 1; chpt1pv.oldAlpha = 1        menuGroup:insert(chpt1pv)        menuGroup.chpt1pv = chpt1pv --nav--navIn--nav         local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH19PG13:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 