       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
        local clack         local CH20PG6  --(2) regular layer        CH20PG6 = display.newImageRect( imageDir.. "p284_ch20pg6.png", 768, 1024 );        CH20PG6.x = 384; CH20PG6.y = 512; CH20PG6.alpha = 1; CH20PG6.oldAlpha = 1        menuGroup:insert(CH20PG6)        menuGroup.CH20PG6 = CH20PG6        local onclackTouch = function(event)           if event.phase=="ended" then               local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( clack_audio, {channel=myChannel} )              end           end        end --(10) regular layer        clack = ui.newButton{            defaultSrc=imageDir.."p284_clack.png",            defaultX = 214,            defaultY = 27,            overSrc=imageDir.."p284_clack.png",            overX = 214,            overY = 27,            onRelease=onclackTouch,            id="clackButton"        }        clack.x = 291; clack.y = 817; clack.alpha = 1; clack.oldAlpha = 1        menuGroup:insert(clack)        menuGroup.clack = clack --pprcrnr--nav--navIn--nav         local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH20PG6:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(clack_audio); clack_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 