    local drawScreen = function()        navigation.DisplayDevelopmentVersion()       menuGroup.navigator = navigation.InsertNavigation{}        local chpt1pv         local chpt1pvover         local zzp         local CH20PG17  --(2) regular layer        CH20PG17 = display.newImageRect( imageDir.. "p295_ch20pg17.png", 768, 1024 );        CH20PG17.x = 384; CH20PG17.y = 512; CH20PG17.alpha = 1; CH20PG17.oldAlpha = 1        menuGroup:insert(CH20PG17)        menuGroup.CH20PG17 = CH20PG17        local onzzpTouch = function(event)           if event.phase=="ended" then               local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( zzp_audio, {channel=myChannel} )              end           end        end --(10) regular layer        zzp = ui.newButton{            defaultSrc=imageDir.."p295_zzp.png",            defaultX = 150,            defaultY = 35,            overSrc=imageDir.."p295_zzp.png",            overX = 150,            overY = 35,            onRelease=onzzpTouch,            id="zzpButton"        }        zzp.x = 197; zzp.y = 642; zzp.alpha = 1; zzp.oldAlpha = 1        menuGroup:insert(zzp)        menuGroup.zzp = zzp        local onchpt1pvTouch = function(event)           if event.phase=="ended" then           media.playVideo( "movie_star.mp4", system.ResourceDirectory, false )           end        end --(10) regular layer        chpt1pv = ui.newButton{            defaultSrc=imageDir.."p295_chpt1pv.png",            defaultX = 102,            defaultY = 102,            overSrc=imageDir.."p295_chpt1pvover.png",            overX = 102,            overY = 102,            onRelease=onchpt1pvTouch,            id="chpt1pvButton"        }        chpt1pv.x = 383; chpt1pv.y = 871; chpt1pv.alpha = 1; chpt1pv.oldAlpha = 1        menuGroup:insert(chpt1pv)        menuGroup.chpt1pv = chpt1pv --pprcrnr--nav--navIn--nav         local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH20PG17:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(zzp_audio); zzp_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 