       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
        local vln         local CH20PG20  --(2) regular layer        CH20PG20 = display.newImageRect( imageDir.. "p298_ch20pg20.png", 768, 1024 );        CH20PG20.x = 384; CH20PG20.y = 512; CH20PG20.alpha = 1; CH20PG20.oldAlpha = 1        menuGroup:insert(CH20PG20)        menuGroup.CH20PG20 = CH20PG20        local onvlnTouch = function(event)           if event.phase=="ended" then               local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( vln_audio, {channel=myChannel} )              end           end        end --(10) regular layer        vln = ui.newButton{            defaultSrc=imageDir.."p298_vln.png",            defaultX = 550,            defaultY = 55,            overSrc=imageDir.."p298_vln.png",            overX = 550,            overY = 55,            onRelease=onvlnTouch,            id="vlnButton"        }        vln.x = 376; vln.y = 487; vln.alpha = 1; vln.oldAlpha = 1        menuGroup:insert(vln)        menuGroup.vln = vln --pprcrnr--nav--navIn--nav         local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH20PG20:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(vln_audio); vln_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 