    local drawScreen = function() 

       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
        local rye         local showhide         local flap         local CH20PG21  --(2) regular layer        CH20PG21 = display.newImageRect( imageDir.. "p299_ch20pg21.png", 768, 1024 );        CH20PG21.x = 384; CH20PG21.y = 512; CH20PG21.alpha = 1; CH20PG21.oldAlpha = 1        menuGroup:insert(CH20PG21)        menuGroup.CH20PG21 = CH20PG21        local onflapTouch = function(event)           if event.phase=="ended" then               local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( flap_audio, {channel=myChannel} )              end                 if showhide.alpha == 0 then                    transitionStash.newTransition_954 = transition.to( showhide, {alpha=showhide.oldAlpha, time=1000, delay=0})                 else                    transitionStash.newTransition_954 = transition.to( showhide, {alpha=0, time=1000, delay=0})                 end           end        end --(10) regular layer        flap = ui.newButton{            defaultSrc=imageDir.."p299_flap.png",            defaultX = 255,            defaultY = 276,            overSrc=imageDir.."p299_flap.png",            overX = 255,            overY = 276,            onRelease=onflapTouch,            id="flapButton"        }        flap.x = 179; flap.y = 229; flap.alpha = 1; flap.oldAlpha = 1        menuGroup:insert(flap)        menuGroup.flap = flap --(2) regular layer        showhide = display.newImageRect( imageDir.. "p299_showhide.png", 412, 246 );        showhide.x = 464; showhide.y = 259; showhide.alpha = 1; showhide.oldAlpha = 1        menuGroup:insert(showhide)        menuGroup.showhide = showhide --(2) regular layer        rye = display.newImageRect( imageDir.. "p299_rye.png", 26, 12 );        rye.x = 433; rye.y = 282; rye.alpha = 1; rye.oldAlpha = 1        menuGroup:insert(rye)        menuGroup.rye = rye --pprcrnr--nav--navIn--nav       showhide.alpha = 0          local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH20PG21:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(flap_audio); flap_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 