       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
        local cles         local CH20PG22  --(2) regular layer        CH20PG22 = display.newImageRect( imageDir.. "p300_ch20pg22.png", 768, 1024 );        CH20PG22.x = 384; CH20PG22.y = 512; CH20PG22.alpha = 1; CH20PG22.oldAlpha = 1        menuGroup:insert(CH20PG22)        menuGroup.CH20PG22 = CH20PG22        local onclesTouch = function(event)           if event.phase=="ended" then               local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( cles_audio, {channel=myChannel} )              end           end        end --(10) regular layer        cles = ui.newButton{            defaultSrc=imageDir.."p300_cles.png",            defaultX = 344,            defaultY = 30,            overSrc=imageDir.."p300_cles.png",            overX = 344,            overY = 30,            onRelease=onclesTouch,            id="clesButton"        }        cles.x = 302; cles.y = 629; cles.alpha = 1; cles.oldAlpha = 1        menuGroup:insert(cles)        menuGroup.cles = cles --pprcrnr--nav--navIn--nav         local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH20PG22:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(cles_audio); cles_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 