       navigation.DisplayDevelopmentVersion()

       menuGroup.navigator = navigation.InsertNavigation{}
        local sptir         local CH21PG21  --(2) regular layer        CH21PG21 = display.newImageRect( imageDir.. "p324_ch21pg21.png", 768, 1024 );        CH21PG21.x = 384; CH21PG21.y = 512; CH21PG21.alpha = 1; CH21PG21.oldAlpha = 1        menuGroup:insert(CH21PG21)        menuGroup.CH21PG21 = CH21PG21        local onsptirTouch = function(event)           if event.phase=="ended" then               local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( sptir_audio, {channel=myChannel} )              end           end        end --(10) regular layer        sptir = ui.newButton{            defaultSrc=imageDir.."p324_sptir.png",            defaultX = 505,            defaultY = 40,            overSrc=imageDir.."p324_sptir.png",            overX = 505,            overY = 40,            onRelease=onsptirTouch,            id="sptirButton"        }        sptir.x = 357; sptir.y = 583; sptir.alpha = 1; sptir.oldAlpha = 1        menuGroup:insert(sptir)        menuGroup.sptir = sptir --pprcrnr--nav--navIn--nav         local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (_G.kwk_currentPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_".._G.kwk_currentPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (_G.kwk_currentPage < _G.kwk_lastPage) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_".._G.kwk_currentPage+1, "moveFromRight")                 end              end           end        end        CH21PG21:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(sptir_audio); sptir_audio = nil                           audio.stop(3); audio.dispose(kwkBindex_audio); kwkBindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 