lpha = 1        menuGroup:insert(house)        menuGroup.house = house        local onLayer1Touch = function(event)           if event.phase=="ended" then              local myClosure_switch = function()                 disposeTweens()                 director:changeScene( "page_2", "fade" )             end             timerStash.newTimer_452 = timer.performWithDelay(0, myClosure_switch, 1)           end        end --(10) regular layer        Layer1 = ui.newButton{            defaultSrc=imgDir.."p7_layer1.png",            defaultX = 72,            defaultY = 80,            overSrc=imgDir.."p7_layer1.png",            overX = 72,            overY = 80,            onRelease=onLayer1Touch,            id="Layer1Button"        }        Layer1.x = 732; Layer1.y = 984; Layer1.alpha = 1; Layer1.oldAlpha = 1        menuGroup:insert(Layer1)        menuGroup.Layer1 = Layer1        local onBforwardTouch = function(event)           if event.phase=="ended" then              local myClosure_switch = function()                 disposeTweens()                 director:changeScene( "page_8", "moveFromRight" )             end             timerStash.newTimer_491 = timer.performWithDelay(0, myClosure_switch, 1)           end        end --(10) regular layer        Bforward = ui.newButton{            defaultSrc=imgDir.."p7_bforward.png",            defaultX = 111,            defaultY = 87,            overSrc=imgDir.."p7_bforward.png",            overX = 111,            overY = 87,            onRelease=onBforwardTouch,            id="BforwardButton"        }        Bforward.x = 475; Bforward.y = 975; Bforward.alpha = 1; Bforward.oldAlpha = 1        menuGroup:insert(Bforward)        menuGroup.Bforward = Bforward        local onBindexTouch = function(event)           if event.phase=="ended" then              local myClosure_switch = function()                 disposeTweens()                 director:changeScene( "page_2", "overFromTop" )             end             timerStash.newTimer_551 = timer.performWithDelay(0, myClosure_switch, 1)              local myChannel = 2              local isChannelPlaying = audio.isChannelPlaying(myChannel)              if isChannelPlaying then                 --nothing              else                 audio.play( Bindex_audio, {channel=myChannel} )              end           end        end --(10) regular layer        Bindex = ui.newButton{            defaultSrc=imgDir.."p7_bindex.png",            defaultX = 66,            defaultY = 69,            overSrc=imgDir.."p7_bindex.png",            overX = 66,            overY = 69,            onRelease=onBindexTouch,            id="BindexButton"        }        Bindex.x = 383; Bindex.y = 969; Bindex.alpha = 1; Bindex.oldAlpha = 1        menuGroup:insert(Bindex)        menuGroup.Bindex = Bindex        local onBbackTouch = function(event)           if event.phase=="ended" then              local myClosure_switch = function()                 disposeAudios()                 disposeTweens()                 director:changeScene( "page_6", "moveFromLeft" )             end             timerStash.newTimer_650 = timer.performWithDelay(0, myClosure_switch, 1)           end        end --(10) regular layer        Bback = ui.newButton{            defaultSrc=imgDir.."p7_bback.png",            defaultX = 111,            defaultY = 88,            overSrc=imgDir.."p7_bback.png",            overX = 111,            overY = 88,            onRelease=onBbackTouch,            id="BbackButton"        }        Bback.x = 293; Bback.y = 973; Bback.alpha = 1; Bback.oldAlpha = 1        menuGroup:insert(Bback)        menuGroup.Bback = Bback --(2) regular layer        showbox = display.newImageRect( imgDir.. "p7_showbox.png", 514, 500 );        showbox.x = 397; showbox.y = 688; showbox.alpha = 1; showbox.oldAlpha = 1        menuGroup:insert(showbox)        menuGroup.showbox = showbox        showbox.alpha = 0          local function flip (event)           local spacer = 180            if event.phase =="ended" then               if event.xStart < event.x and (event.x - event.xStart) >= spacer then                 if (curPage > 1) then                      disposeAudios()                     disposeTweens()                     director:changeScene( "page_"..curPage-1, "moveFromLeft" )                 end              elseif event.xStart > event.x and (event.xStart-event.x) >= spacer then                  if (curPage < numPages) then                      disposeAudios()                     disposeTweens()                     director:changeScene("page_"..curPage+1, "moveFromRight")                 end              end           end        end        BG:addEventListener("touch", flip)    end    drawScreen()    function disposeAudios(event)                    audio.stop(2); audio.dispose(Bindex_audio); Bindex_audio = nil                        end    function disposeTweens(event)       cancelAllTweens();       cancelAllTimers();       cancelAllTransitions();    end    return menuGroup end 